<?phpclass Index extends Controller{    function __construct()    {        parent::__construct();        $this->view->parent = "index";    }    function index() {        if(!Session::get('loggedIn')) {            $this->view->render('index/index');        } else {            $this->view->render('index/loggedin');        }    }    function details() {        $this->view->render('index/index');    }    function loginRun()    {        $this->model->run($this);    }    // Register functionality if required at a later date.//    function register()//    {//        $this->model->register($this);//    }}