<?phpclass Bootstrap{    function __construct() {        $url = isset($_GET['url']) ? $_GET['url'] : null;        $url = rtrim($url, '/');        $url = explode('/', $url);        //debugging code        //print_r($url);        if (empty($url[0])) {            require 'controllers' . DIRECTORY_SEPARATOR . 'index.php';            $controller = new Index();            $controller->index();            return false;        }        $file = 'controllers' . DIRECTORY_SEPARATOR . $url[0] . '.php';        if (file_exists($file)) {            require $file;        } else {            //Debug            //echo $file;            require 'controllers' . DIRECTORY_SEPARATOR . 'error.php';            $controller = new Errors();            $controller->index();            return false;        }        $controller = new $url[0];        $controller->loadModel($url[0]);        // calling methods area        if (isset($url[2])) {            if (method_exists($controller, $url[1])) {                $controller->{$url[1]}($url[2]);            } else {                $this->error();            }        } else {            if (isset($url[1])) {                if (method_exists($controller, $url[1])) {                    $controller->{$url[1]}();                } else {                    $this->error();                }            } else {                $controller->index();            }        }    }    function error() {        require 'controllers/error.php';        $controller = new Errors();        $controller->index();        return false;    }}